insert into VarietyShow(name,type,episodes,region,director,actor,post,introduction,recommend) values('高能少年团2','1','36','1','丁卿','王俊凯/董子健/杨紫','./varietyshow/GAONENGSHAONIANTUAN2.jpg,./varietyshow/GAONENGSHAONIANTUANDAHAIBAO.jpg','《高能少年团》是浙江卫视的一档室内加户外多场景式的偶像对抗挑战类真人秀。节目以明星真人秀的形式记录每个少年的高能挑战，意想不到的对手、青春奇幻的互动、绝不能输的挑战，力争展现出少年们的朝气蓬勃。第二季将以“一首歌、一座城”为线索，让高能少年们在不同城市的风土人情中体味冒险与成长。','0,1');
insert into VarietyShow(name,type,episodes,region,director,actor,post,introduction,recommend) values('极限挑战3','1','24','1','严敏','黄渤/孙红雷/罗志祥','./recommend/JIXIANTIAOZHAN3.jpg','《极限挑战》是东方卫视打造的一档大型原创励志星素互动综艺节目。本季黄渤、孙红雷、黄磊、罗志祥、王迅、张艺兴组成的“极限男人帮”原班人马再度登场，将继续延续以往正能量理念，与普通百姓“亲密接触”，深入民众当中体验各种职业，寻找“唤醒城市的人”。','1,99');
insert into VarietyShow(name,type,episodes,region,director,actor,post,introduction,recommend) values('极限挑战4','1','24','1','严敏','黄渤/孙红雷/罗志祥','./varietyshow/JIXIANTIAOZHAN4.jpg,./recommend/JIXIANTIAOZHANDAHAIBAO.jpg','《极限挑战》是东方卫视打造的一档大型原创励志星素互动综艺节目。第四季以“向美好生活，出发”为主题，描绘各行各业的“奋斗”姿态，向新时代致敬。第四季节目将有各行各业的精英强势加入，以更强的星素互动拉近与观众之间的距离，让路人的参与感更强，同时通过强化素人经历，传递出积极向上的奋斗力量，进一步强化“亲民暖综”的定位，带给观众更多的陪伴意义以及更强的亲切感和认同感。','0,7');
insert into VarietyShow(name,type,episodes,region,director,actor,post,introduction,recommend) values('这就是灌篮1','1','24','1','朱玲','周杰伦/易建联/李易峰','./varietyshow/ZHEJIUSHIGUANLAN1.jpg','《这就是灌篮》是一档由优酷&浙江卫视联合播出的中国首档大型青春篮球竞技原创节目。周杰伦&林书豪、李易峰&郭艾伦分别担任两大篮球阵营领队，集结热爱篮球的年轻人，引领全民篮球狂潮。四位领队，两支战队，上演最燃3V3篮球对战。用最真实的逐梦之路，唤醒青春热血。','1,99');
