insert into VarietyShow(name,type,episodes,region,director,actor,post,introduction,recommend) values('中国好声音2018','2','13','1','金磊','周杰伦/谢霆锋/李健/庾澄庆','./recommend/ZHONGGUOHAOSHENGYIN2018.jpg','《中国好声音》是由浙江卫视和灿星制作联手打造的音乐励志评论类节目。节目旨在为中国乐坛的发展提供一批怀揣梦想、具有天赋才华的音乐人。周杰伦、庾澄庆、谢霆锋、李健四位导师通过盲选选择自己心仪的学员组成战队，并一路带领自己的战队学员力争赢得冠军。','99');
