insert into VarietyShow(name,type,episodes,region,director,actor,post,introduction,recommend) values('完美的餐厅','1,3','13','1','林芯翼','薛凯琪/王菊/陈立农/黄明昊','./recommend/WANMEIDECANTING.jpg','《完美的餐厅》是由兴格传媒、优酷平台、口碑app共同打造的节目。节目视线聚焦于青年人的创业生活。','2,99');
insert into VarietyShow(name,type,episodes,region,director,actor,post,introduction,recommend) values('锋味2018','1,3','12','1','高伟恩','谢霆锋','./varietyshow/FENGWEI2018.jpg','《锋味2018》是一档大型生活类户外美食节目。谢霆锋邀请多位明星好友，共同前往希腊米克诺斯岛、捷克布拉格、俄罗斯圣彼得堡、土耳其伊斯坦布尔、成都、厦门等国内外著名美食旅游都市，品尝各种特色美食，体验各地独特的风土人情与当地人的生活方式。每一集还将走近一位当地人的日常生活，并在他家的厨房内，为他量身打造一道专属的“锋味”，讲述“料理与人”之间的精彩故事。','99');
insert into VarietyShow(name,type,episodes,region,director,actor,post,introduction,recommend) values('花儿与少年1','1,4','8','1','廖珂','刘涛/许晴/张翰','./varietyshow/HUAERYUSHAONIAN1.jpg','《花儿与少年第一季》是湖南卫视推出的大型明星姐弟自助远行真人秀节目，节目共8期。第一季节目七位固定成员是郑佩佩、张凯丽、许晴、刘涛、李菲儿、张翰、华晨宇。节目中两位“少年”带领五位“姐姐”，开启一段为期十五天从意大利到西班牙的没有经纪人、不准带助理、每天生活费有限的限制下，在人生地不熟，甚至语言都根本不通的国度，完成一段异域的背包奇妙之旅。','99');
insert into VarietyShow(name,type,episodes,region,director,actor,post,introduction,recommend) values('花儿与少年2','1,4','11','1','廖珂','许晴/宁静/杨洋','./varietyshow/HUAERYUSHAONIAN2.jpg','《花儿与少年第二季》是湖南卫视推出的大型明星姐弟自助远行真人秀节目，节目共11期。第二季节目七位固定成员是毛阿敏、许晴、宁静、陈意涵、郑爽、井柏然、杨洋。该节目由“花少姐弟团”展开一场前往英国、土耳其、阿联酋的没有经纪人、不准带助理、每天生活费有限的限制下，在人生地不熟，甚至语言都根本不通的国度，完成一段异域的背包奇妙之旅。','4,99');
insert into VarietyShow(name,type,episodes,region,director,actor,post,introduction,recommend) values('花儿与少年3','1,4','12','1','吴梦知','江疏影/古力娜扎','./varietyshow/HUAERYUSHAONIAN3.jpg','《花儿与少年第三季》是湖南卫视推出的青春伙伴自助远行真人秀节目，共12期。第三季节目八位固定成员是江疏影、古力娜扎、赖雨濛、宋祖儿、杨祐宁、陈柏霖、张若昀、井柏然。节目以“青春伙伴”的形式远赴赤道以南，游经巴西、南非、澳大利亚、纳米比亚四国，开启了一场别开生面的洲际穿越之旅。','4,99');
insert into VarietyShow(name,type,episodes,region,director,actor,post,introduction,recommend) values('挑战吧！太空','1,5','12','1','云天','王宝强/田亮/张雨绮','./recommend/TIAOZHANBATAIKONG1.jpg','《挑战吧！太空》节目由王宝强、张雨绮、田亮、吴宣仪、朱正廷、陈锴杰六位嘉宾作为“太空C计划”志愿者参与了节目拍摄，体验中国航天员训练体系，并前往火星模拟真实环境进行封闭式生存挑战。','2,4,99');
